mapper.select:
select order_label_id, label_name, status, create_time
from lm_order_label
where order_label_id = #{orderLabelId}

mapper.insert:
insert into lm_order_label(
    label_name, status, create_time
) values (
             #{labelName}, #{status,jdbcType=VARCHAR}, sysdate()
         )

mapper.update:
update lm_order_label
label_name = #{labelName}, status = #{status},
where order_label_id = #{orderLabelId}
