mapper.select:
SELECT o.order_id, o.order_number, o.order_type, o.order_status, o.order_label, o.customer_id, o.employee_id, o.order_price, o.order_remark, o.create_user_id, o.create_time, c.customer_name as customerName, c.customer_phone as customerPhone, c.customer_wx_number as customerWxNumber, u.nick_name AS employeeName FROM lm_order o LEFT JOIN lm_customer c ON o.customer_id = c.customer_id LEFT JOIN sys_user u ON o.employee_id = u.user_id
AND o.order_number LIKE CONCAT('%', #{orderNumber}, '%')
AND o.order_type = #{orderType}
AND o.order_status = #{orderStatus}
AND o.employee_id = #{employeeId}
AND (c.customer_name LIKE CONCAT('%', #{customerName}, '%') OR c.customer_phone LIKE CONCAT('%', #{customerName}, '%') OR c.customer_wx_number LIKE CON CONCAT('%', #{customerName}, '%'))
AND o.create_time >= #{beginTime}
AND o.create_time <= #{endTime}

mapper.update:
UPDATE lm_order
order_type = #{orderType}, order_status = #{orderStatus}, order_label = #{orderLabel}, customer_id = #{customerId}, employee_id = #{employeeId}, order_price = #{orderPrice}, order_remark = #{orderRemark},
WHERE
order_id = #{orderId}
order_id in
#{item}

UPDATE lm_order
SET employee_id = #{employeeId}
WHERE order_id = #{orderId}
